import React, { useState } from 'react';
import axios from 'axios';

const InfoForm=() => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [workExperience, setWorkExperience] = useState('');
  const [qualifications, setQualification] = useState('');
  const [role, setRole] = useState('');
  const [techSkills, setTechSkills] = useState('');
  const [businessImpact, setBusinessImpact] = useState('');
  const [coursesTaught, setCoursesTaken] = useState('');
  const [yearsOfExperience, setYearsExperience] = useState('');
  const [photo, setPhoto] = useState(null);

  const handleSubmit = async (event) => {
      event.preventDefault();

      if (!firstName || !lastName || !email || !role) {
          alert('Please fill in all required fields');
          return;
        }

      if (role === 'developer' && (!techSkills || !businessImpact)) {
      alert('Please fill in all required fields for the developer role');
      return;
      }

      if (role === 'tutor' && (!coursesTaught || !yearsOfExperience)) {
      alert('Please fill in all required fields for the tutor role');
      return;
      }

      const formData = {
          firstName,
          lastName,
          email,
          workExperience,
          qualifications,
          role,
          techSkills: role === 'developer' ? techSkills : undefined,
          businessImpact: role === 'developer' ? businessImpact : undefined,
          coursesTaught: role === 'tutor' ? coursesTaught : undefined,
          yearsOfExperience: role === 'tutor' ? yearsOfExperience : undefined,
      };

      // console.log(formData);

      try {
        const response = await axios.post('http://localhost:8085/api/resume/create', formData);
        console.log(response.data);
        alert('Resume created successfully');
      } catch (error) {
        console.error(error);
        alert('An error occurred. Please try again later');
      }
    };



  const handleRoleChange = (e) => {
    setRole(e.target.value);
  };


  return (
    <form onSubmit={handleSubmit}>
      <div className="space-y-25 px-8 pt-32 pb-32 bg-cyan-950	text-slate-50	">
        <div className="border-b border-gray-900/10 pb-10 mx-72 " >
          <h2 className="text-base font-semibold leading-7 text-gray-900 text-slate-50	">Create Resume</h2>
          <p className="mt-1 text-sm leading-6 text-gray-600 text-slate-50	">
            This information will be used to Create Resume
          </p>
        </div>
