import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, DialogPanel } from '@headlessui/react'
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline'

const navigation = [
  { name: 'Product', href: '#' },
  { name: 'Features', href: '#' },
  { name: 'Marketplace', href: '#' },
  { name: 'Company', href: '#' },
]
const InfoForm=() => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [workExperience, setWorkExperience] = useState('');
  const [qualifications, setQualification] = useState('');
  const [role, setRole] = useState('');
  const [techSkills, setTechSkills] = useState('');
  const [businessImpact, setBusinessImpact] = useState('');
  const [coursesTaught, setCoursesTaken] = useState('');
  const [yearsOfExperience, setYearsExperience] = useState('');
  const [photo, setPhoto] = useState(null);

  const handleSubmit = async (event) => {
      event.preventDefault();

      if (!firstName || !lastName || !email || !role) {
          alert('Please fill in all required fields');
          return;
        }

      if (role === 'developer' && (!techSkills || !businessImpact)) {
      alert('Please fill in all required fields for the developer role');
      return;
      }

      if (role === 'tutor' && (!coursesTaught || !yearsOfExperience)) {
      alert('Please fill in all required fields for the tutor role');
      return;
      }
      let photoUrl = '';
    if (photo) {
      const formData = new FormData();
      formData.append('file', photo);
      try {
        const uploadResponse = await axios.post('http://localhost:8085/api/resume/upload-photo', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        photoUrl = uploadResponse.data;
      } catch (error) {
        console.error('Error uploading photo', error);
        alert('Error uploading photo');
        return;
      }
    }

      const formData = {
          firstName,
          lastName,
          email,
          workExperience,
          qualifications,
          role,
          techSkills: role === 'developer' ? techSkills : undefined,
          businessImpact: role === 'developer' ? businessImpact : undefined,
          coursesTaught: role === 'tutor' ? coursesTaught : undefined,
          yearsOfExperience: role === 'tutor' ? yearsOfExperience : undefined,
          photoUrl,
      };

      // console.log(formData);

      try {
        const response = await axios.post('http://localhost:8085/api/resume/create', formData);
        console.log(response.data);
        alert('Resume created successfully');
      } catch (error) {
        console.error(error);
        alert('An error occurred. Please try again later');
      }
    };



  const handleRoleChange = (e) => {
    setRole(e.target.value);
  };
  const handlePhotoChange = (e) => {
    setPhoto(e.target.files[0]);
  };


  return (
    <>
<body>
<div className="bg-white">
      <div className="relative isolate px-6 pt-14 lg:px-8">
        <div
          className="absolute inset-x-0 -top-40 -z-10 transform-gpu overflow-hidden blur-3xl sm:-top-80"
          aria-hidden="true"
        >
          <div
            className="relative left-[calc(50%-11rem)] aspect-[1155/678] w-[36.125rem] -translate-x-1/2 rotate-[30deg] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%-30rem)] sm:w-[72.1875rem]"
            style={{
              clipPath:
                'polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)',
            }}
          />
        </div>

        <div
          className="absolute inset-x-0 top-[calc(100%-13rem)] -z-10 transform-gpu overflow-hidden blur-3xl sm:top-[calc(100%-30rem)]"
          aria-hidden="true"
        >
          <div
            className="relative left-[calc(50%+3rem)] aspect-[1155/678] w-[36.125rem] -translate-x-1/2 bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%+36rem)] sm:w-[72.1875rem]"
            style={{
              clipPath:
                'polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)',
            }}
          />
        </div>
      </div>
    </div>
</body>


    </>

  );
}

export default InfoForm;
