import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Qualifications = () => {
  const { role } = useParams();
  const [qualifications, setQualifications] = useState([]);

  useEffect(() => {
    const fetchQualifications = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/resume/qualification/${role}`);
        setQualifications(response.data);
      } catch (error) {
        console.error('Error fetching qualifications', error);
      }
    };

    fetchQualifications();
  }, [role]);

  return (
    <div>
      <h2>Qualifications for {role}</h2>
      <ul>
        {qualifications.map((qualification, index) => (
          <li key={index}>
            <p>Name: {qualification.firstName} {qualification.lastName}</p>
            <p>Qualifications: {qualification.qualifications}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Qualifications;
